INSERT INTO `teachers` VALUES (8, '杨庚', '男', '15820482838', '南京邮电大学', '杨庚，男，江苏建湖人，教授，博士生导师。先后在南京航空航天大学、加拿大ORTECH公司、Laval大学GIREF研究中心、南京Motolora软件中心等高校和软件公司任职。');
INSERT INTO `teachers` VALUES (9, '朱大勇', '男', '13028374827', '电子科技大学', '朱大勇，博士，讲师，可信云计算与大数据实验室大医学图像处理研究方向负责人，主持四川省科技厅应用基础重点项目1项、参与863、国家自然科学基金面上项目等多项国家项目。');
INSERT INTO `teachers` VALUES (10, '林子雨', '女', '12937393728', '厦门大学', '厦门大学计算机科学系教师，编著国内高校第一本系统性介绍大数据知识专业教材《大数据技术原理与应用》，建设国内高校首个大数据课程公共服务平台，为教师和学生提供全方位一站式免费服务。');
INSERT INTO `teachers` VALUES (11, '郭炜', '男', '17362927392', '北京大学', '北京大学信息学院教师，担任北京大学ACM国际大学生程序设计竞赛队教练，研究方向：计算机辅助教学，北京角斗士软件技术有限公司创始人，开发多款英语学习软件');
INSERT INTO `teachers` VALUES (12, '刘钦', '男', '18273927297', '南京大学', '刘钦，博士，讲师。主要从事软件工程的教学与科研工作。先后获得国家级教学成果奖、IBM中国优秀教师奖、Google 奖教金等奖项。教授软件工程与计算 I，软件工程与计算II和移动互联网应用开发等课程。');
INSERT INTO `teachers` VALUES (13, '龚沛曾', '男', '18293729273', '同济大学', '同济大学电信学院计算机科学与技术系教授，计算机和信息技术教学实验中心副主任；教育部大学计算机课程教学指导委员会副主任，上海市计算机基础教育协会理事长，全国高校计算机基础教育研究会副会长。');
INSERT INTO `teachers` VALUES (14, '杨志强', '男', '18982178223', '同济大学', '博士，同济大学电子与信息工程学院教授，同济大学计算机与信息技术国家级教学实验示范中心基础平台主任，华东地区高校计算机教育研究会常务理事，全国高校计算机基础教育研究会理工委员会副主任。');
INSERT INTO `teachers` VALUES (15, '何钦铭', '男', '17272922829', '浙江大学', '浙江大学计算机科学与技术学院教授，教育部大学计算机课程教学指导委员会副主任委员、浙江省高校计算机类专业教学指导委员会主任委员。主要研究方向为数据挖掘、虚拟计算系统技术。');
INSERT INTO `teachers` VALUES (16, '贝佳', '女', '18263937209', '南江大学', '副教授，近年来承担3门本科生课程，分别是《面向服务的软件工程》、《服务计算与SOA开发》和《商务智能》均为本科高年级专业课程。');
INSERT INTO `teachers` VALUES (17, '王移芝', '女', '18720037197', '北京交通大学', '国家教学名师，北京交通大学计算机与信息学院教授，计算机基础教学学科带头人，国家级与北京市优秀教学团队“计算机基础系列课程建设”负责人。');
INSERT INTO `teachers` VALUES (18, '宋麟', '女', '17320283028', '武汉大学', '博士，现任武汉大学计算机学院讲师。主要从事视频、动画处理与人工智能等课程的教学和研究。先后在华中师范大学、武汉大学获得理学学士、硕士、工学博士学位。');
INSERT INTO `teachers` VALUES (19, '杨璠', '男', '15203928392', '中南财经政法大学', '博士，副教授，毕业于武汉大学计算机学院，获计算机科学与技术专业博士学位，现任信息与安全工程学院计算机科学与技术系副主任。长期从事数据科学、网络空间安全技术领域的教学和科研研究。');
INSERT INTO `teachers` VALUES (20, '唐大仕', '男', '15239382937', '北京大学', '唐大仕，博士，北京大学信息科学技术学院教师，有丰富的软件开发经验，在Java、C#程序设计方面有多年的教学经验，获北京大学教学成果一等奖。');
